import React, { useState } from "react";
import { useEffect } from "react";
import { getProducts } from "./api";
import Pagination from "./Pagination";
import ProductList from "./ProductList";

function ProductPage() {
const [loading, setLoading] = useState(true);
const [data, setData] = useState({});
const [page, setPage] = useState(1);

useEffect(()=>{
  getUsers({page});
}, [page])

function getUsers({page}){
  setLoading(true);
  return fetch(`https://dbioz2ek0e.execute-api.ap-south-1.amazonaws.com/mockapi/get-products?page=${page}`).then(res=> res.json())
  .then((res)=>{
    setLoading(false);
    setData(res)
  });
}
if(loading){
  return <div className="loading"></div>;
}
console.log(page)

  return (
    <div>
      <h1 data-testid="product-page-title">Product Page</h1>
      <button data-testid="low-to-high">Sort low to high</button>
      <button data-testid="high-to-low">Sort high to low</button>
      <div>
        <label>Per page</label>
        <select data-testid="limit-select"></select>
      </div>
      <Pagination />
      {/* map products */}
      <ProductList />
      {data.data.map((i))}
    </div>
  );
}

export default ProductPage;
